/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author Egor Kulikov (egor@egork.net)
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        TaskB solver = new TaskB();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskB {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();
            int m = in.readInt();

            Map<Integer, Musketeer> musketeers = new HashMap<>(n);
            for (int i = 0; i < n; i++) musketeers.put(i + 1, new Musketeer());
            for (int i = 0; i < m; i++) {
                int a = in.readInt();
                int b = in.readInt();
                Musketeer musketeerA = musketeers.get(a);
                Musketeer musketeerB = musketeers.get(b);
                musketeerA.addFriend(musketeerB);
                musketeerB.addFriend(musketeerA);
            }

            int ans = Integer.MAX_VALUE;
            for (Musketeer m1 : musketeers.values()) {
                for (Musketeer m2 : m1.getFriends()) {
                    if (!m1.isFriendOf(m2)) continue;
                    for (Musketeer m3 : m2.getFriends()) {
                        if (!m1.isFriendOf(m3) || !m2.isFriendOf(m3)) continue;
                        int m1Known = m1.getFriendCountWithout(m2, m3);
                        int m2Known = m2.getFriendCountWithout(m1, m3);
                        int m3Known = m3.getFriendCountWithout(m1, m2);
                        ans = Math.min(m1Known + m2Known + m3Known, ans);
                    }
                }
            }

            out.print(ans == Integer.MAX_VALUE ? -1 : ans);
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (numChars == -1)
                throw new InputMismatchException();
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0)
                    return -1;
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9')
                    throw new InputMismatchException();
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null)
                return filter.isSpaceChar(c);
            return isWhitespace(c);
        }

        public static boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }

    static class Musketeer {
        private final Set<Musketeer> friends = new HashSet<>();

        public Set<Musketeer> getFriends() {
            return friends;
        }

        public boolean isFriendOf(Musketeer m) {
            return friends.contains(m);
        }

        public int getFriendCountWithout(Musketeer a, Musketeer b) {
            int sz = friends.size();
            if (friends.contains(a)) sz--;
            if (friends.contains(b)) sz--;
            return sz;
        }

        public void addFriend(Musketeer m) {
            friends.add(m);
        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void close() {
            writer.close();
        }

        public void print(int i) {
            writer.print(i);
        }

    }
}

