            int[] a = new int[n];
            for (int i = 0; i < arr.Length; i++)
            {
                a[i] = Convert.ToInt32(arr[i]);
            }


            int ovDiff = 0;
            while (FindMax(a) != 0)
            {
                int x = FindMax(a);
                //int diff = CountDiff(a[0], a[x]);
                //a[0] += diff;
                //a[x] -= diff;

                a[0]++;
                a[x]--;
                ovDiff++;
            }

            Console.WriteLine(ovDiff);
            //Console.ReadKey();
        }

        static int FindMax(int[] a)
        {
            int x = 0;
            int max = a[0];
            for (int i = 1; i < a.Length; i++)
            {
                if (a[i] >= max)
                {
                    max = a[i];
                    x = i;
                }
            }

            return x;
        }

        static int CountDiff(int a, int b)
        {
            if (a == b) return 1;
            return Convert.ToInt32(Math.Ceiling((double)((b - a) / 2)));
        }
    }
}

